const slider=new Flickity(document.getElementById("slider"),{wrapAround:!0,cellAlign:"left",contain:!0,autoPlay:5e3,prevNextButtons:!1,pageDots:!1}),body=document.querySelector("body"),colors=[{bgColor:"js-background-color-1",textColor:"js-text-color-1"},{bgColor:"js-background-color-2",textColor:"js-text-color-2"},{bgColor:"js-background-color-3",textColor:"js-text-color-1"},{bgColor:"js-background-color-4",textColor:"js-text-color-2"}];let previousColor=0,currentColor=1;slider.on("select",o=>{body.classList.remove(colors[previousColor].bgColor),body.classList.remove(colors[previousColor].textColor),body.classList.add(colors[currentColor].bgColor),body.classList.add(colors[currentColor].textColor)}),slider.on("change",o=>{currentColor!==colors.length-1?currentColor+=1:currentColor=0,previousColor!==colors.length-1?previousColor+=1:previousColor=0}),document.getElementById("previous").addEventListener("click",()=>slider.previous()),document.getElementById("next").addEventListener("click",()=>slider.next());