window.addEventListener("DOMContentLoaded",()=>{const o=new Flickity(document.getElementById("slider"),{wrapAround:!0,cellAlign:"left",contain:!0,autoPlay:5e3,prevNextButtons:!1,pageDots:!1}),e=document.querySelector("body"),t=[{bgColor:"js-background-color-1",textColor:"js-text-color-1"},{bgColor:"js-background-color-2",textColor:"js-text-color-2"},{bgColor:"js-background-color-3",textColor:"js-text-color-1"},{bgColor:"js-background-color-4",textColor:"js-text-color-2"}];let l=0,r=1;o.on("select",o=>{e.classList.remove(t[l].bgColor),e.classList.remove(t[l].textColor),e.classList.add(t[r].bgColor),e.classList.add(t[r].textColor)}),o.on("change",o=>{r!==t.length-1?r+=1:r=0,l!==t.length-1?l+=1:l=0}),document.getElementById("previous").addEventListener("click",()=>o.previous()),document.getElementById("next").addEventListener("click",()=>o.next())});